getByUsername.reduce:
function(keys, values, rereduce) {
  var temp = {};
  for(i in values){
    var game = values[i][0];
    var score = values[i][1];
    if(temp[game]){
      if(parseInt(temp[game]) < parseInt(score)){
        temp[game] = score;
      }
    } else {
      temp[game] = score;
    }
  }
  return temp;
}

